mapper.select:

/*HlpdskMapper.selectTotalHlpdskListCount*/
  SELECT 
  COUNT(A.REQUST_SJ) AS requstSj    
  FROM TB_HLPDSK A
  JOIN TB_USER_DETAIL B ON A.REGISTER_ID = B.EMP_NO
  LEFT OUTER JOIN TB_CMMN_CD C 
  ON 
  (
   C.CD = A.REQUST_CMPNY_CD AND
   C.CLS_CD = '0024' AND
   C.USE_AT = 'Y'
  )
  WHERE 1 = 1


          AND A.REQUST_CMPNY_CD = #{requstCmpnyCd}


          AND A.PROCESS_STTUS_CD = #{processSttusCd}


          AND A.REQUST_SE = #{requstSe}


           AND B.[EMP_NM] LIKE CONCAT('%', #{requstUserId}, '%')


           AND CONVERT(VARCHAR,A.REG_DT,112) BETWEEN #{startDt} AND #{lastDt}







              AND A.REQUST_SJ like '%' + #{requstSj} + '%'

/*HlpdskMapper.selectHlpdskList*/ SELECT REQUST_SN AS requstSn , A.REQUST_SJ AS requstSj , A.REQUST_CN AS requstCn , A.REQUST_SE AS requstSe , ISNULL(C.CD_NM, '미등록 회사') AS cmpNm , A.REQUST_CMPNY_CD AS requstCmpnyCd , B.EMP_NM AS empNm , A.REGISTER_ID AS requstUserId , CONVERT(VARCHAR,A.REG_DT,120) AS regDt , A.REQUST_FILE_GRP_NO AS requstFileGrpNo , A.PROCESS_STTUS_CD AS processSttusCd , A.PROCESS_DE AS processDe , A.REGISTER_ID AS registerId , A.REG_DT AS regDt , A.UPDUSR_ID AS updusrId , A.UPDT_DT AS updtDt FROM TB_HLPDSK A JOIN TB_USER_DETAIL B ON A.REGISTER_ID = B.EMP_NO LEFT OUTER JOIN TB_CMMN_CD C ON ( C.CD = A.REQUST_CMPNY_CD AND C.CLS_CD = '0024' AND C.USE_AT = 'Y' ) WHERE 1 = 1
AND A.REQUST_CMPNY_CD = #{requstCmpnyCd}
AND A.PROCESS_STTUS_CD = #{processSttusCd}
AND A.REQUST_SE = #{requstSe}
AND B.[EMP_NM] LIKE CONCAT('%', #{requstUserId}, '%')
AND CONVERT(VARCHAR,A.REG_DT,112) BETWEEN #{startDt} AND #{lastDt}
AND A.REQUST_SJ like '%' + #{requstSj} + '%'
ORDER BY A.REQUST_SN DESC OFFSET #{pageInfo.firstRecordIndex} ROWS FETCH NEXT #{pageInfo.recordCountPerPage} ROWS ONLY;

mapper.insert:
      INSERT
      INTO TB_HLPDSK (
            REQUST_SJ	        , REQUST_CN	        , REQUST_SE	        , REQUST_CMPNY_CD	        , REQUST_USER_ID
	        , REQUST_FILE_GRP_NO	        , PROCESS_STTUS_CD	        , PROCESS_DE	        , PROCESS_CN	        , PROCESS_USER_ID
	        , PROCESS_FILE_GRP_NO	        , REGISTER_ID	        , REG_DT	        , UPDUSR_ID	        , UPDT_DT  

	    ) VALUES (	    
	        #{requstSj}	        , #{requstCn}	        , #{requstSe}	        , #{requstCmpnyCd}	        , #{txUserId}        
	        , #{requstFileGrpNo}        	        , '01'	        , ''	        , ''	        , ''
	        , ''	        , #{txUserId}	        , GETDATE()	        , #{txUserId}	        , GETDATE()
	    )

mapper.update:
UPDATE TB_HLPDSK SET
REQUST_SJ = #{requstSj}
, REQUST_SE = #{requstSe}
, REQUST_CN = #{requstCn}      
, UPDUSR_ID = #{txUserId}
, REQUST_FILE_GRP_NO = #{requstFileGrpNo}
, UPDT_DT = GETDATE()      
WHERE REQUST_SN = #{requstSn}

mapper.select:
SELECT COUNT(REQUST_SJ)
FROM TB_HLPDSK A 
JOIN TB_USER_BAS B ON A.REGISTER_ID = B.USER_ID


        AND REQUST_CMPNY_CD = #{requstCmpnyCd}


        AND PROCESS_STTUS_CD = #{processSttusCd}


        AND REQUST_SE = #{requstSe}


        AND USER_NM like '%' + #{userNm} + '%'



            AND REQUST_SJ like '%' + #{requstSj} + '%'




            AND CONVERT(VARCHAR,A.REG_DT,112) BETWEEN #{startDt} AND #{lastDt}


             AND A.REG_DT > GETDATE()-91

/*HlpdskMapper.getReceiptList*/ SELECT REQUST_SN AS requstSn , A.REQUST_SJ AS requstSj , A.REQUST_CN AS requstCn , A.REQUST_SE AS requstSe , A.REQUST_CMPNY_CD AS requstCmpnyCd , A.REGISTER_ID AS requstUserId , CONVERT(VARCHAR,A.REG_DT,120) AS regDt , A.REQUST_FILE_GRP_NO AS requstFileGrpNo , A.PROCESS_STTUS_CD AS processSttusCd , A.PROCESS_DE AS processDe , A.PROCESS_CN AS processCn , A.REGISTER_ID AS registerId , A.REG_DT AS regDt , A.UPDUSR_ID AS updusrId , A.UPDT_DT AS updtDt , B.EMP_NM AS userNm FROM TB_HLPDSK A JOIN TB_USER_DETAIL B ON A.REGISTER_ID = B.EMP_NO
AND REQUST_CMPNY_CD = #{requstCmpnyCd}
AND PROCESS_STTUS_CD = #{processSttusCd}
AND REQUST_SE = #{requstSe}
AND USER_NM like '%' + #{userNm} + '%'
AND CONVERT(VARCHAR,A.REG_DT,112) BETWEEN #{startDt} AND #{lastDt}
AND A.REG_DT > GETDATE()-91
AND REQUST_SJ like '%' + #{requstSj} + '%'
ORDER BY REQUST_SN DESC OFFSET #{pageInfo.firstRecordIndex} ROWS FETCH NEXT #{pageInfo.recordCountPerPage} ROWS ONLY;

mapper.update:
/*HlpdskMapper.updateReceip*/
      UPDATE TB_HLPDSK SET
      PROCESS_STTUS_CD = #{processSttusCd}
      , PROCESS_CN = #{processCn}
      , PROCESS_TM = #{processTm}
      , UPDUSR_ID = #{txUserId}
      , PROCESS_USER_ID = #{txUserId}
      , PROCESS_FILE_GRP_NO = #{processFileGrpNo}
      , UPDT_DT = GETDATE()
      , PROCESS_DE = #{processDe}      
      WHERE REQUST_SN = #{requstSn}

mapper.select:
/*HlpdskMapper.selectRequstSnSj*/
SELECT A.REQUST_SN, 
(SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0036' AND CD = #{requstSe}) AS CD_NM,
(SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = #{requstCmpnyCd} ) AS CMP_NM
FROM [rpa_potal].[dbo].[TB_HLPDSK] A
WHERE A.REQUST_SN = (SELECT MAX(B.REQUST_SN) FROM [rpa_potal].[dbo].[TB_HLPDSK] B);
