mapper.select:
/* JobMapper.selectJobArList */
DECLARE @start_date DATETIME ,@end_date DATETIME ,@NOW_DATE DATETIME SET @start_date = #{startDe}; SET @start_date = CONVERT(DATETIME, CONVERT(varchar(10), DATEPART ( yyyy , @start_date )) + '-' + CONVERT(varchar(10), DATEPART( mm , @start_date )) +'-' + CONVERT(varchar(10), DATEPART( dd , @start_date ))); SET @end_date = #{endDe}; SET @end_date = CONVERT(DATETIME,CONVERT(varchar(10), DATEPART ( yyyy , @end_date )) + '-' + CONVERT(varchar(10), DATEPART( mm , @end_date )) +'-' + CONVERT(varchar(10), DATEPART( dd , @end_date )) + ' 23:59:59'); SET @NOW_DATE = DATEADD(Hour, -9, GETDATE());
SELECT A.[SN] ,A.[CMPNY_CD] ,C.CD_NM AS CMPNY_NM ,A.[PROCESS_CD] ,(SELECT NAME FROM SY_ROBOTS WHERE ID = A.[ROBOT_ID]) ROBOT_NAME ,A.[ROBOT_ID] ,A.[INPUT_ARGUMENTS] ,(SELECT A_1.PROCESS_NM FROM TB_TASK_DEVLOP_PROCESS A_1 WHERE A_1.PROCESS_STTUS = 'O1' AND A_1.PROCESS_CD = A.PROCESS_CD) AS VIEW_PROCESS_NAME ,A.[PROCESS_NAME] ,A.[PROCESS_KEY] ,A.[STTUS] ,B.CD_NM AS STTUS_NAME ,CONVERT(CHAR(19),A.[CREAT_DT] ,20) AS CREAT_DT ,CONVERT(CHAR(19),A.[START_DT] ,20) AS START_DT ,CONVERT(CHAR(19),A.[END_DT] ,20) AS END_DT ,A.[EXCEPTION_MSSAGE] ,A.[UPDUSR_ID] ,A.[UPDT_DT]
FROM [dbo].[TB_TASK_DEVLOP_PROCESS_ATTENDED_SCHDULE] A JOIN TB_CMMN_CD B ON ( A.STTUS = B.CD AND B.CLS_CD = '0053' AND B.USE_AT = 'Y' ) JOIN TB_CMMN_CD C ON ( A.CMPNY_CD = C.CD AND C.CLS_CD = '0024' AND C.USE_AT = 'Y' ) WHERE 1 = 1
AND A.[CMPNY_CD] = #{cmpnyCd}
AND (CONVERT(DATETIME, A.[CREAT_DT]) >= CONVERT(DATETIME, @start_date)
AND CONVERT(DATETIME, A.[CREAT_DT]) <= CONVERT(DATETIME, @end_date))
AND A.[STTUS] = #{state}
AND (SELECT NAME FROM SY_ROBOTS WHERE ID = A.[ROBOT_ID]) = #{searchRobotName}
ORDER BY A.[CREAT_DT] DESC OFFSET #{paginationInfo.firstRecordIndex} ROWS FETCH NEXT #{paginationInfo.recordCountPerPage} ROWS ONLY

/* JobArMapper.selectJobArListCount */

DECLARE 
    @start_date  DATETIME ,@end_date  DATETIME ,@NOW_DATE  DATETIME 
SET @start_date = #{startDe}; 
SET @start_date = CONVERT(DATETIME, CONVERT(varchar(10), DATEPART ( yyyy , @start_date )) + '-' + CONVERT(varchar(10), DATEPART( mm , @start_date ))  +'-' +  CONVERT(varchar(10), DATEPART( dd , @start_date )));
SET @end_date = #{endDe}; 
SET @end_date = CONVERT(DATETIME,CONVERT(varchar(10), DATEPART ( yyyy , @end_date )) + '-' + CONVERT(varchar(10), DATEPART( mm , @end_date ))  +'-' +  CONVERT(varchar(10), DATEPART( dd , @end_date )) + ' 23:59:59');
SET @NOW_DATE = DATEADD(Hour, -9, GETDATE()); 

  SELECT COUNT(A.[PROCESS_CD])
  FROM [dbo].[TB_TASK_DEVLOP_PROCESS_ATTENDED_SCHDULE] A
  JOIN   TB_CMMN_CD B
    ON   (
                    A.STTUS = B.CD
                AND B.CLS_CD = '0053'
                AND B.USE_AT = 'Y'
            )
    JOIN   TB_CMMN_CD C
    ON   (
                    A.CMPNY_CD = C.CD
                AND C.CLS_CD = '0024'
                AND C.USE_AT = 'Y'
            )
     WHERE   1 = 1

        AND   A.[CMPNY_CD] = #{cmpnyCd}


       AND  (CONVERT(DATETIME, A.[CREAT_DT]) >= CONVERT(DATETIME, @start_date)


       AND   CONVERT(DATETIME, A.[CREAT_DT]) <= CONVERT(DATETIME, @end_date))


       AND   A.[STTUS] = #{state}


        AND (SELECT NAME FROM SY_ROBOTS WHERE ID = A.[ROBOT_ID]) = #{searchRobotName}

            SELECT STTUS
			FROM TB_TASK_DEVLOP_PROCESS_ATTENDED_SCHDULE
			WHERE SN = #{sn}

mapper.update:
	    /*JobArMapper.convertToStopped*/
	    UPDATE [RPA_POTAL].[DBO].[TB_TASK_DEVLOP_PROCESS_ATTENDED_SCHDULE]
		SET [STTUS] = 'STOPPED', END_DT = CONVERT(CHAR(23), GETDATE(), 21)

            1 = 1
            AND [STTUS] = 'STOP'

				AND [SN] = #{sn}


				AND [CMPNY_CD] = #{cmpnyCd}


				AND [PROCESS_CD] = #{processCd}
