mapper.select:
/* PoUserHistMngMapper.getPoUserHistMngList */
SELECT JOBS_ID AS JobsId , CMPNY_NM AS CmpnyNm , PROCESS_CD AS ProcessCd , PROCESS_NM AS ProcessNm , (SELECT TOP 1 CD_NM FROM TB_CMMN_CD WHERE CLS_CD = 15 AND CD = STTUS) AS Sttus , TRGET_AT AS TrgetAt , RETRY_TRGET_ID AS RetryTrgetId , ORIGINAL_AT AS OriginalAt , CONVERT(CHAR(19), START_DT, 21) AS StartDt , CONVERT(CHAR(19), END_DT, 21) AS EndDt , [BOT_NM] AS botNm FROM TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST WHERE 1 = 1
AND CMPNY_CD = #{cmpnyCd}
AND STTUS = #{searchProcessState}
AND PROCESS_CD = #{searchProcessCd}
AND PROCESS_NM = #{searchProcessNm}
ORDER BY JOBS_ID DESC OFFSET #{paginationInfo.firstRecordIndex} ROWS FETCH NEXT #{paginationInfo.recordCountPerPage} ROWS ONLY

/* PoUserHistMngMapper.getPoUserHistMngCount */

SELECT COUNT(JOBS_ID)
FROM TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST
WHERE 1 = 1 	


	AND  CMPNY_CD = #{cmpnyCd}


	AND STTUS = #{searchProcessState}


	AND PROCESS_CD = #{searchProcessCd}


	AND PROCESS_NM = #{searchProcessNm}

mapper.update:
/* PoUserHistMngMapper.updateTargetInfo */
UPDATE TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST
SET TRGET_AT = 0
WHERE JOBS_ID = #{targetId}

/* PoUserHistMngMapper.updateOriginalInfo */
UPDATE TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST
SET TRGET_AT = 0 , RETRY_TRGET_ID = #{targetId} , ORIGINAL_AT = 0
WHERE JOBS_ID = #{jobsId}

/* PoUserHistMngMapper.updateJobInfo */
UPDATE TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST
SET TRGET_AT = 1 , RETRY_TRGET_ID = 0 , ORIGINAL_AT = 1
WHERE JOBS_ID = #{jobsId}
