mapper.select:
/* ExecutStatsMapper.selectProcessRunStsList */
SET ANSI_WARNINGS OFF SET ARITHIGNORE ON SET ARITHABORT OFF
SELECT * FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , SUM(M01) AS M01, SUM(M02) AS M02, SUM(M03) AS M03 , SUM(M04) AS M04, SUM(M05) AS M05, SUM(M06) AS M06 , SUM(M07) AS M07, SUM(M08) AS M08, SUM(M09) AS M09 , SUM(M10) AS M10, SUM(M11) AS M11, SUM(M12) AS M12 , SUM(MON_TOT) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, SUM(SUESS_CNT) AS SUESS_CNT, 'A' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , SUESS_CNT FROM TB_STS_TOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD = '5'
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) AS result PIVOT ( SUM(SUESS_CNT) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, SUM(FAIL_CNT) AS FAIL_CNT, 'B' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , FAIL_CNT FROM TB_STS_TOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) AS result PIVOT ( SUM(FAIL_CNT) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12, 0 AS MON_TOT FROM ( SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'C' AS GBN , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS SU_PER FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_TOT WHERE YMD like CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) AS result PIVOT ( SUM(SU_PER) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result
UNION ALL
SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, SUM(MON_TOT) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'C' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12 , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS MON_TOT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_TOT WHERE YMD like CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, PROCESS_CD, PROCESS_NM, 'A' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, PROCESS_CD, PROCESS_NM, 'B' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, PROCESS_CD, PROCESS_NM, 'C' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) T WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN ) ZZ WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN
UNION ALL
SELECT 'ZZ' AS COMP_CD , 'ZZ' AS COMP_NM , 'ZZ' AS PROCESS_CD , 'ZZ' AS PROCESS_NMGBN , GBN , SUM(M01) AS M01, SUM(M02) AS M02, SUM(M03) AS M03 , SUM(M04) AS M04, SUM(M05) AS M05, SUM(M06) AS M06 , SUM(M07) AS M07, SUM(M08) AS M08, SUM(M09) AS M09 , SUM(M10) AS M10, SUM(M11) AS M11, SUM(M12) AS M12 , SUM(MON_TOT) AS MON_TOT FROM ( SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, SUM(SUESS_CNT) AS SUESS_CNT, 'TA' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , SUESS_CNT FROM TB_STS_TOT WHERE YMD LIKE CONCAT(#{searchYear},'%') AND STATE_CD = '5'
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM ) AS result PIVOT ( SUM(SUESS_CNT) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, SUM(FAIL_CNT) AS FAIL_CNT, 'TB' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , FAIL_CNT FROM TB_STS_TOT WHERE YMD LIKE CONCAT(#{searchYear},'%') AND STATE_CD IN ('2', '3', '4', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM ) AS result PIVOT ( SUM(FAIL_CNT) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12, 0 AS MON_TOT FROM ( SELECT YM, 'TC' AS GBN , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS SU_PER FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_TOT WHERE YMD like CONCAT(#{searchYear},'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM ) AS result PIVOT ( SUM(SU_PER) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result
UNION ALL
SELECT GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, SUM(MON_TOT) AS MON_TOT FROM ( SELECT 'TC' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12 , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS MON_TOT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_TOT WHERE YMD like CONCAT(#{searchYear},'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1
UNION ALL
SELECT 'TA' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT(#{searchYear},'%')
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
SELECT 'TB' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT(#{searchYear},'%')
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
SELECT 'TC' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TOT WHERE YMD LIKE CONCAT(#{searchYear},'%')
AND COMP_CD = #{searchCmpnyCd}
) T WHERE 1= 1 GROUP BY GBN ) ZZ WHERE 1= 1 GROUP BY GBN ) Q ORDER BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN

/* TaskManageMapper.selectProcessMonthList */ SET ANSI_WARNINGS OFF SET ARITHIGNORE ON SET ARITHABORT OFF SELECT * FROM (
SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , SUM(D01) AS D01, SUM(D02) AS D02, SUM(D03) AS D03, SUM(D04) AS D04, SUM(D05) AS D05 , SUM(D06) AS D06, SUM(D07) AS D07, SUM(D08) AS D08, SUM(D09) AS D09, SUM(D10) AS D10 , SUM(D11) AS D11, SUM(D12) AS D12, SUM(D13) AS D13, SUM(D14) AS D14, SUM(D15) AS D15 , SUM(D16) AS D16, SUM(D17) AS D17, SUM(D18) AS D18, SUM(D19) AS D19, SUM(D20) AS D20 , SUM(D21) AS D21, SUM(D22) AS D22, SUM(D23) AS D23, SUM(D24) AS D24, SUM(D25) AS D25 , SUM(D26) AS D26, SUM(D27) AS D27, SUM(D28) AS D28, SUM(D29) AS D29, SUM(D30) AS D30 , SUM(D31) AS D31, SUM(MON_TOT) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31 AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM ( SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, SUM(SUESS_CNT) AS SUESS_CNT, 'A' AS GBN FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_C