SELECT GBN, D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , ROUND(CONVERT(FLOAT , (D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT GBN , EXECUT_CNT , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM ( SELECT YM, AVG(EXECUT_TM) AS EXECUT_TM, 'TD' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , DATEDIFF(SS , A.START_TIME , A.END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(YMD)) FROM TB_STS_PROC B WHERE 1=1 AND YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')) AS EXECUT_CNT FROM TB_STS_PROC A WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM , EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
