SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , SUM(M01) AS M01, SUM(M02) AS M02, SUM(M03) AS M03 , SUM(M04) AS M04, SUM(M05) AS M05, SUM(M06) AS M06 , SUM(M07) AS M07, SUM(M08) AS M08, SUM(M09) AS M09 , SUM(M10) AS M10, SUM(M11) AS M11, SUM(M12) AS M12 , SUM(MON_TOT) AS MON_TOT FROM ( -- A SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, SUM(SUESS_CNT) AS SUESS_CNT, 'A' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.PROCESS_CD , A.PROCESS_NM , SUESS_CNT FROM TB_STS_PROC A INNER JOIN TB_TASK_DEVLOP_PROCESS B ON A.PROCESS_CD = B.PROCESS_CD WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD = '5' -- AND B.EXECUT_SE IS NULL
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.PROCESS_CD , A.PROCESS_NM , SUESS_CNT FROM TB_STS_PROC A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD = '5'
