-- D SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , ROUND(CONVERT(FLOAT , (M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , EXECUT_CNT , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, AVG(EXECUT_TM) AS EXECUT_TM , 'D' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.PROCESS_CD , A.PROCESS_NM , DATEDIFF(SS , START_TIME , END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(CONCAT('M', SUBSTRING(YMD,6,2)))) FROM TB_STS_PROC B WHERE B.PROCESS_CD = A.PROCESS_CD AND YMD LIKE CONCAT(#{searchYear},'-','%')) AS EXECUT_CNT FROM TB_STS_PROC A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- E SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , TOTAL_TM AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , TOTAL_TM , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'E' AS GBN , MAX(REDCN_TM) AS REDCN_TM , TOTAL_TM FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , ROUND(REDCN_TM,2) AS REDCN_TM , REDCN_TM AS TOTAL_TM FROM TB_STS_PROC WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM , TOTAL_TM ) AS result PIVOT ( MAX(REDCN_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- F SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , REDCN_RATE_GROUP , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'F' AS GBN , MAX(REDCN_RATE) AS REDCN_RATE , REDCN_RATE_GROUP FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , REDCN_TM , JOB_TM , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE_GROUP FROM TB_STS_PROC WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM , REDCN_RATE_GROUP ) AS result PIVOT ( MAX(REDCN_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- G SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'G' AS GBN , SUM(OPER_TM) AS OPER_TM FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM FROM TB_STS_PROC WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) AS result PIVOT ( SUM(OPER_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- H SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, 'H' AS GBN , SUM(OPER_RATE) AS OPER_RATE FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , PROCESS_CD , PROCESS_NM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM , CASE WHEN DATEDIFF(SS , START_TIME , END_DT) = 0 THEN NULL ELSE CONVERT(NUMERIC(13,3), CONVERT(FLOAT,DATEDIFF(SS , START_TIME , END_DT)) / 86400 * 100) END AS OPER_RATE FROM TB_STS_PROC WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM ) AS result PIVOT ( SUM(OPER_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
) ZZ WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, PROCESS_CD, PROCESS_NM, GBN
UNION ALL
