SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_DEV_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
AND A.JOB_ID IN (SELECT MAX(JOB_ID) FROM TB_TASK_DEVLOP_PROCESS_DEV_RETRY GROUP BY RETRY_TRGET_ID)
) S WHERE 1 = 1 GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM
UNION ALL -- C SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, ROBOT_ID, BOT_NM, 'A' AS GBN , 0 AS D01, 0 AS D02, 0 AS D03, 0 AS D04, 0 AS D05, 0 AS D06, 0 AS D07, 0 AS D08, 0 AS D09, 0 AS D10 , 0 AS D11, 0 AS D12, 0 AS D13, 0 AS D14, 0 AS D15, 0 AS D16, 0 AS D17, 0 AS D18, 0 AS D19, 0 AS D20 , 0 AS D21, 0 AS D22, 0 AS D23, 0 AS D24, 0 AS D25, 0 AS D26, 0 AS D27, 0 AS D28, 0 AS D29, 0 AS D30, 0 AS D31 , 0 AS MON_TOT FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, ROBOT_ID, BOT_NM, 'B' AS GBN , 0 AS D01, 0 AS D02, 0 AS D03, 0 AS D04, 0 AS D05, 0 AS D06, 0 AS D07, 0 AS D08, 0 AS D09, 0 AS D10 , 0 AS D11, 0 AS D12, 0 AS D13, 0 AS D14, 0 AS D15, 0 AS D16, 0 AS D17, 0 AS D18, 0 AS D19, 0 AS D20 , 0 AS D21, 0 AS D22, 0 AS D23, 0 AS D24, 0 AS D25, 0 AS D26, 0 AS D27, 0 AS D28, 0 AS D29, 0 AS D30, 0 AS D31 , 0 AS MON_TOT FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, ROBOT_ID, BOT_NM, 'C' AS GBN , 0 AS D01, 0 AS D02, 0 AS D03, 0 AS D04, 0 AS D05, 0 AS D06, 0 AS D07, 0 AS D08, 0 AS D09, 0 AS D10 , 0 AS D11, 0 AS D12, 0 AS D13, 0 AS D14, 0 AS D15, 0 AS D16, 0 AS D17, 0 AS D18, 0 AS D19, 0 AS D20 , 0 AS D21, 0 AS D22, 0 AS D23, 0 AS D24, 0 AS D25, 0 AS D26, 0 AS D27, 0 AS D28, 0 AS D29, 0 AS D30, 0 AS D31 , 0 AS MON_TOT FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM ) T WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN
UNION ALL
-- D SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , ROUND(CONVERT(FLOAT , (D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , EXECUT_CNT , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM (
SELECT YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, AVG(EXECUT_TM) AS EXECUT_TM , 'D' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , DATEDIFF(SS , START_TIME , END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(YMD)) FROM TB_STS_PROC B WHERE B.PROCESS_CD = A.PROCESS_CD AND YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')) AS EXECUT_CNT FROM TB_STS_BOT A WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- E SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, D01 , D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , TOTAL_TM AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , TOTAL_TM , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM (
SELECT YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, 'E' AS GBN , MAX(REDCN_TM) AS REDCN_TM , TOTAL_TM FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , ROBOT_ID , BOT_NM , ROUND(REDCN_TM,2) AS REDCN_TM , REDCN_TM AS TOTAL_TM FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, TOTAL_TM ) AS result PIVOT ( MAX(REDCN_TM) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- F SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, D01 , D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , REDCN_RATE_GROUP , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM (
SELECT YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, 'F' AS GBN , MAX(REDCN_RATE) AS REDCN_RATE , REDCN_RATE_GROUP FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , ROBOT_ID , BOT_NM , REDCN_TM , JOB_TM , CONVERT(NUMERIC(13,1),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE , CONVERT(NUMERIC(13,1),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE_GROUP FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND JOB_TM IS NOT NULL AND JOB_TM != 0 AND REDCN_TM IS NOT NULL --
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, REDCN_RATE_GROUP ) AS result PIVOT ( MAX(REDCN_RATE) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- G SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, D01 , D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM (
