AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM ) AS result PIVOT ( SUM(OPER_TM) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- H SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, D01 , D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM (
SELECT YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, 'H' AS GBN , SUM(OPER_RATE) AS OPER_RATE FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , ROBOT_ID , BOT_NM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM , CASE WHEN DATEDIFF(SS , START_TIME , END_DT) = 0 THEN NULL ELSE CONVERT(NUMERIC(13,3), CONVERT(FLOAT,DATEDIFF(SS , START_TIME , END_DT)) / 86400 * 100) END AS OPER_RATE FROM TB_STS_BOT WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM ) AS result PIVOT ( SUM(OPER_RATE) FOR YM IN (D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31)) AS pivot_result ) AA WHERE 1 = 1
) ZZ WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN
UNION ALL
SELECT 'ZZ' AS COMP_CD , 'ZZ' AS COMP_NM , 00 AS ROBOT_ID , 'ZZ' AS BOT_NM , GBN , SUM(D01) AS D01, SUM(D02) AS D02, SUM(D03) AS D03, SUM(D04) AS D04, SUM(D05) AS D05 , SUM(D06) AS D06, SUM(D07) AS D07, SUM(D08) AS D08, SUM(D09) AS D09, SUM(D10) AS D10 , SUM(D11) AS D11, SUM(D12) AS D12, SUM(D13) AS D13, SUM(D14) AS D14, SUM(D15) AS D15 , SUM(D16) AS D16, SUM(D17) AS D17, SUM(D18) AS D18, SUM(D19) AS D19, SUM(D20) AS D20 , SUM(D21) AS D21, SUM(D22) AS D22, SUM(D23) AS D23, SUM(D24) AS D24, SUM(D25) AS D25 , SUM(D26) AS D26, SUM(D27) AS D27, SUM(D28) AS D28, SUM(D29) AS D29, SUM(D30) AS D30 , SUM(D31) AS D31, SUM(MON_TOT) AS MON_TOT FROM ( SELECT GBN, D01, D02, D03, D04, D05, D06, D07, D08, D09, D10, D11, D12, D13, D14, D15, D16, D17, D18, D19, D20, D21, D22, D23, D24, D25, D26, D27, D28, D29, D30, D31 , D01+D02+D03+D04+D05+D06+D07+D08+D09+D10+D11+D12+D13+D14+D15+D16+D17+D18+D19+D20+D21+D22+D23+D24+D25+D26+D27+D28+D29+D30+D31 AS MON_TOT FROM ( SELECT GBN , ISNULL(D01,0) AS D01, ISNULL(D02,0) AS D02, ISNULL(D03,0) AS D03 , ISNULL(D04,0) AS D04, ISNULL(D05,0) AS D05, ISNULL(D06,0) AS D06 , ISNULL(D07,0) AS D07, ISNULL(D08,0) AS D08, ISNULL(D09,0) AS D09 , ISNULL(D10,0) AS D10, ISNULL(D11,0) AS D11, ISNULL(D12,0) AS D12 , ISNULL(D13,0) AS D13, ISNULL(D14,0) AS D14, ISNULL(D15,0) AS D15 , ISNULL(D16,0) AS D16, ISNULL(D17,0) AS D17, ISNULL(D18,0) AS D18 , ISNULL(D19,0) AS D19, ISNULL(D20,0) AS D20, ISNULL(D21,0) AS D21 , ISNULL(D22,0) AS D22, ISNULL(D23,0) AS D23, ISNULL(D24,0) AS D24 , ISNULL(D25,0) AS D25, ISNULL(D26,0) AS D26, ISNULL(D27,0) AS D27 , ISNULL(D28,0) AS D28, ISNULL(D29,0) AS D29, ISNULL(D30,0) AS D30 , ISNULL(D31,0) AS D31 FROM ( SELECT YM, SUM(SUESS_CNT) AS SUESS_CNT, 'TA' AS GBN FROM ( SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , SUESS_CNT FROM TB_STS_BOT A INNER JOIN TB_TASK_DEVLOP_PROCESS B ON A.PROCESS_CD = B.PROCESS_CD WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND STATE_CD = '5' -- AND B.EXECUT_SE IS NULL
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , SUESS_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND STATE_CD = '5'
AND COMP_CD = #{searchCmpnyCd}
AND B.ORIGINAL_AT = 1 AND A.JOB_ID NOT IN ( SELECT DISTINCT(RETRY_TRGET_ID) FROM TB_TASK_DEVLOP_PROCESS_OPER_RETRY)
UNION ALL
SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , SUESS_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND STATE_CD = '5'
AND COMP_CD = #{searchCmpnyCd}
AND A.JOB_ID IN (SELECT MAX(JOB_ID) FROM TB_TASK_DEVLOP_PROCESS_OPER_RETRY GROUP BY RETRY_TRGET_ID)
UNION ALL
SELECT YMD , CONCAT('D', SUBSTRING(YMD,9,2)) AS YM , SUESS_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_DEV_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_DEV_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT(#{searchYear},'-',#{month},'%') AND STATE_CD = '5'
