-- C (TOTAL) SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, SUM(MON_TOT) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, 'C' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12 , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS MON_TOT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_BOT A INNER JOIN TB_TASK_DEVLOP_PROCESS B ON A.PROCESS_CD = B.PROCESS_CD WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6') AND B.EXECUT_SE IS NULL
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
AND B.ORIGINAL_AT = 1 AND A.JOB_ID NOT IN ( SELECT DISTINCT(RETRY_TRGET_ID) FROM TB_TASK_DEVLOP_PROCESS_OPER_RETRY)
UNION ALL
SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_BOT A LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_EXECUT_HIST B ON A.JOB_ID = B.JOBS_ID LEFT OUTER JOIN TB_TASK_DEVLOP_PROCESS_OPER_RETRY C ON A.JOB_ID = C.JOB_ID WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6')
AND COMP_CD = #{searchCmpnyCd}
AND A.JOB_ID IN (SELECT MAX(JOB_ID) FROM TB_TASK_DEVLOP_PROCESS_OPER_RETRY GROUP BY RETRY_TRGET_ID)
