-- D SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , ROUND(CONVERT(FLOAT , (M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , EXECUT_CNT , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, AVG(EXECUT_TM) AS EXECUT_TM , 'D' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.ROBOT_ID , A.BOT_NM , DATEDIFF(SS , START_TIME , END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(CONCAT('M', SUBSTRING(YMD,6,2)))) FROM TB_STS_BOT B WHERE B.ROBOT_ID = A.ROBOT_ID AND YMD LIKE CONCAT(#{searchYear},'-','%')) AS EXECUT_CNT FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- E SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , TOTAL_TM AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , TOTAL_TM , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
