-- G SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, ROBOT_ID, BOT_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
