SELECT 'TF' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12 , 0 AS MON_TOT FROM TB_STS_BOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
