SELECT 'TH' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12 , 0 AS MON_TOT FROM TB_STS_BOT WHERE YMD LIKE CONCAT( #{searchYear} ,'%') ) T WHERE 1= 1 GROUP BY GBN
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , ROUND(CONVERT(FLOAT , (M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT GBN , EXECUT_CNT , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, AVG(EXECUT_TM) AS EXECUT_TM, 'TD' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , DATEDIFF(SS , A.START_TIME , A.END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(CONCAT('M', SUBSTRING(YMD,6,2)))) FROM TB_STS_BOT B WHERE 1=1 AND YMD LIKE CONCAT(#{searchYear},'-',#{month},'%')) AS EXECUT_CNT FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM , EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , TOTAL_TM AS MON_TOT FROM ( SELECT GBN , TOTAL_TM , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, MIN(REDCN_TM) AS EXECUT_TM, 'TE' AS GBN , TOTAL_TM FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , ROUND(REDCN_TM,2) AS REDCN_TM , REDCN_TM AS TOTAL_TM FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM , TOTAL_TM ) AS result PIVOT ( MIN(EXECUT_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT GBN , REDCN_RATE_GROUP , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, MIN(REDCN_RATE) AS REDCN_RATE, 'TF' AS GBN , REDCN_RATE_GROUP FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE_GROUP FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%') -- AND JOB_TM IS NOT NULL AND JOB_TM != 0 AND REDCN_TM IS NOT NULL
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM , REDCN_RATE_GROUP ) AS result PIVOT ( MIN(REDCN_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, SUM(OPER_TM) AS OPER_TM, 'TG' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM ) AS result PIVOT ( SUM(OPER_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, SUM(OPER_RATE) AS OPER_RATE, 'TH' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , CASE WHEN DATEDIFF(SS , START_TIME , END_DT) = 0 THEN NULL ELSE CONVERT(NUMERIC(13,3), CONVERT(FLOAT,DATEDIFF(SS , START_TIME , END_DT)) / 86400 * 100) END AS OPER_RATE FROM TB_STS_BOT A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM ) AS result PIVOT ( SUM(OPER_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1 ) ZZ WHERE 1= 1
GROUP BY GBN ORDER BY ZZ.COMP_CD, ZZ.ROBOT_ID, ZZ.GBN --

