SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, TASK_CD, TASK_NM, 'B' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, TASK_CD, TASK_NM
UNION ALL
SELECT COMP_CD, (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM, TASK_CD, TASK_NM, 'C' AS GBN , 0 AS M01, 0 AS M02, 0 AS M03, 0 AS M04, 0 AS M05, 0 AS M06, 0 AS M07, 0 AS M08, 0 AS M09, 0 AS M10, 0 AS M11, 0 AS M12, 0 AS MON_TOT FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
GROUP BY COMP_CD, COMP_NM, TASK_CD, TASK_NM ) T WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN
UNION ALL
-- D SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , ROUND(CONVERT(FLOAT , (M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12)) / EXECUT_CNT , 2) AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN , EXECUT_CNT , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, AVG(EXECUT_TM) AS EXECUT_TM , 'D' AS GBN , EXECUT_CNT FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , A.TASK_CD , A.TASK_NM , DATEDIFF(SS , START_TIME , END_DT) AS EXECUT_TM , ( SELECT COUNT(DISTINCT(CONCAT('M', SUBSTRING(YMD,6,2)))) FROM TB_STS_TASK B WHERE B.TASK_CD = A.TASK_CD AND YMD LIKE CONCAT(#{searchYear},'-','%')) AS EXECUT_CNT FROM TB_STS_TASK A WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, EXECUT_CNT ) AS result PIVOT ( AVG(EXECUT_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- E SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , TOTAL_TM AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN , TOTAL_TM , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, 'E' AS GBN , MAX(REDCN_TM) AS REDCN_TM , TOTAL_TM FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , TASK_CD , TASK_NM , ROUND(REDCN_TM,2) AS REDCN_TM , REDCN_TM AS TOTAL_TM FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM , TOTAL_TM ) AS result PIVOT ( MAX(REDCN_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- F SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN , REDCN_RATE_GROUP , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, 'F' AS GBN , MAX(REDCN_RATE) AS REDCN_RATE , REDCN_RATE_GROUP FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , TASK_CD , TASK_NM , REDCN_TM , JOB_TM , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE , CONVERT(NUMERIC(13,0),(REDCN_TM / JOB_TM) * 100) AS REDCN_RATE_GROUP FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM , REDCN_RATE_GROUP ) AS result PIVOT ( MAX(REDCN_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- G SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, 'G' AS GBN , SUM(OPER_TM) AS OPER_TM FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , TASK_CD , TASK_NM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM ) AS result PIVOT ( SUM(OPER_TM) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
UNION ALL
-- H SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT --, REDCN_RATE_GROUP AS MON_TOT FROM ( SELECT COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM (
SELECT YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM, 'H' AS GBN , SUM(OPER_RATE) AS OPER_RATE FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , COMP_CD , (SELECT CD_NM FROM [rpa_potal].[dbo].[TB_CMMN_CD] WHERE CLS_CD = '0024' AND CD = COMP_CD) AS COMP_NM , TASK_CD , TASK_NM , DATEDIFF(SS , START_TIME , END_DT) AS OPER_TM , CASE WHEN DATEDIFF(SS , START_TIME , END_DT) = 0 THEN NULL ELSE CONVERT(NUMERIC(13,3), CONVERT(FLOAT,DATEDIFF(SS , START_TIME , END_DT)) / 86400 * 100) END AS OPER_RATE FROM TB_STS_TASK WHERE YMD LIKE CONCAT( #{searchYear} ,'%')
AND COMP_CD = #{searchCmpnyCd}
) S WHERE 1 = 1 GROUP BY YM, COMP_CD, COMP_NM, TASK_CD, TASK_NM ) AS result PIVOT ( SUM(OPER_RATE) FOR YM IN (M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12)) AS pivot_result ) AA WHERE 1 = 1
) ZZ WHERE 1= 1 GROUP BY COMP_CD, COMP_NM, TASK_CD, TASK_NM, GBN
UNION ALL
SELECT 'ZZ' AS COMP_CD , 'ZZ' AS COMP_NM , 'ZZ' AS TASK_CD , 'ZZ' AS TASK_NM , GBN , SUM(M01) AS M01, SUM(M02) AS M02, SUM(M03) AS M03 , SUM(M04) AS M04, SUM(M05) AS M05, SUM(M06) AS M06 , SUM(M07) AS M07, SUM(M08) AS M08, SUM(M09) AS M09 , SUM(M10) AS M10, SUM(M11) AS M11, SUM(M12) AS M12 , SUM(MON_TOT) AS MON_TOT FROM ( SELECT GBN, M01, M02, M03, M04, M05, M06, M07, M08, M09, M10, M11, M12 , M01+M02+M03+M04+M05+M06+M07+M08+M09+M10+M11+M12 AS MON_TOT FROM ( SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12 FROM ( SELECT YM, SUM(SUESS_CNT) AS SUESS_CNT, 'TA' AS GBN FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , SUESS_CNT FROM TB_STS_TASK A INNER JOIN TB_TASK_DEVLOP_PROCESS B ON A.PROCESS_CD = B.PROCESS_CD WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD = '5' AND B.EXECUT_SE IS NULL
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
