UNION ALL
SELECT GBN , ISNULL(M01,0) AS M01, ISNULL(M02,0) AS M02, ISNULL(M03,0) AS M03 , ISNULL(M04,0) AS M04, ISNULL(M05,0) AS M05, ISNULL(M06,0) AS M06 , ISNULL(M07,0) AS M07, ISNULL(M08,0) AS M08, ISNULL(M09,0) AS M09 , ISNULL(M10,0) AS M10, ISNULL(M11,0) AS M11, ISNULL(M12,0) AS M12, 0 AS MON_TOT FROM ( SELECT YM, 'TC' AS GBN , ROUND((CONVERT(FLOAT, SUM(SUESS_CNT)) / CONVERT(FLOAT,SUM(TOT_CNT)) )*100,0) AS SU_PER FROM ( SELECT YMD , CONCAT('M', SUBSTRING(YMD,6,2)) AS YM , SUESS_CNT , FAIL_CNT , FAIL_CNT + SUESS_CNT AS TOT_CNT FROM TB_STS_TASK A INNER JOIN TB_TASK_DEVLOP_PROCESS B ON A.PROCESS_CD = B.PROCESS_CD WHERE YMD LIKE CONCAT( #{searchYear} ,'%') AND STATE_CD IN ('2', '3', '4', '5', '6') AND B.EXECUT_SE IS NULL
AND COMP_CD = #{searchCmpnyCd}
UNION ALL
